const FloatCard = props => {
  const { rotateIndex, selfIndex, bgColor, position, floatCard } = { ...props };

  return (
    <Animated.View
      style={[
        {
          position: "absolute",
          height: 200,
          width: 200,
          borderRadius: 10,
          backgroundColor: bgColor,
          shadowOffset: { width: 2, height: 2 },
          shadowOpacity: 0.2,
          shadowRadius: 3,
          left: "25%",
          zIndex: -999
        },
        position
      ]}
    />
  );
};
