export default class DemoScreen extends React.Component {
  state = {
    animation: new Animated.Value(0),
    position: new Animated.Value(-200),
    rotateIndex: 0,
    rotateDeg: "90deg"
  };

  //NOTE è¿™ä¸ªåœ°æ–¹++çš„ä½ç½®,å¦‚æžœåœ¨åŽé¢,ç¬¬ä¸€æ¬¡åŠ¨ç”»ä¸ä¼šæ˜¾ç¤º,++åœ¨å‰é¢å°±æ²¡æœ‰ä»€ä¹ˆé—®é¢˜äº†

  rotateWheel = () => {
    Animated.timing(this.state.animation, {
      toValue: ++this.state.rotateIndex,
      duration: 500
    }).start(() => {
      if (this.state.rotateIndex > 3) {
        this.setState({ rotateIndex: 0 });
        this.state.animation.setValue(0);
      }
      if (this.state.rotateIndex == 3) {
        this.floatCard();
      } else {
        this.hiddenCard();
      }
    });
  };

  floatCard = () => {
    Animated.timing(this.state.position, {
      toValue: 400,
      duration: 800
    }).start();
  };

  hiddenCard = () => {
    Animated.timing(this.state.position, {
      toValue: -200,
      duration: 900
    }).start();
  };
  render() {
    const rotateStyle = {
      transform: [
        {
          rotate: this.state.animation.interpolate({
            inputRange: [0, 1, 2, 3, 4],
            outputRange: ["0deg", "90deg", "180deg", "270deg", "360deg"]
          })
        }
      ]
    };

    const position = {
      bottom: this.state.position
    };
    return (
      <View style={styles.container}>
        <View
          style={{
            marginTop: 500,
            alignItems: "center",
            justifyContent: "center"
          }}
        >
          <TouchableWithoutFeedback onPress={this.rotateWheel}>
            <Animated.Image
              source={require("../assets/images/astro/knob.png")}
              style={[ImageStyle, rotateStyle]}
            />
          </TouchableWithoutFeedback>
          <Image
            source={require("../assets/images/astro/purplecircle.png")}
            style={ImageStyle2}
          />
        </View>

        {/* <Animated.View
          style={[
            {
              position: "absolute",
              height: 200,
              width: 200,
              borderRadius: 10,
              backgroundColor: "rgba(43, 196, 248, 1.000)",
              shadowOffset: { width: 2, height: 2 },
              shadowOpacity: 0.2,
              shadowRadius: 3,
              left: "25%",
              zIndex: -999
            },
            CardPosition
          ]}
        /> */}
        <FloatCard
          position={position}
          bgColor={"rgba(22, 157, 218, 1.000)"}
          selfIndex={1}
          rotateIndex={this.state.rotateIndex}
          floatCard={this.floatCard}
        />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center"
  }
});

